document.addEventListener('DOMContentLoaded', function() {
    const loginBtn = document.getElementById('loginBtn');
    if (loginBtn) {
        loginBtn.addEventListener('click', async () => {
            if (typeof window.loginWithGoogle === 'function') {
                try { await window.loginWithGoogle(); } catch (e) { console.warn('Login error:', e); }
            } else {
                // Fallback: open modal if function is not ready yet
                try { document.getElementById('loginModal')?.classList.add('show'); } catch {}
            }
        });
    }

    // Toggle dropdown al clickear todo el recuadro de perfil
    const userProfile = document.getElementById('userProfile');
    if (userProfile) {
        userProfile.addEventListener('click', (e) => {
            // Evita que clics en enlaces internos (como logout) abran/cieran sin querer
            const isInteractive = e.target.closest('a, button, .dropdown-item');
            if (isInteractive && !e.target.closest('#dropdownMenu')) return;
            if (typeof window.toggleDropdown === 'function') window.toggleDropdown();
        });
        // Accesible con teclado
        userProfile.addEventListener('keydown', (e) => {
            if (e.key === 'Enter' || e.key === ' ') {
                e.preventDefault();
                if (typeof window.toggleDropdown === 'function') window.toggleDropdown();
            }
        });
    }

    // Logout desde el menú
    const logoutBtn = document.getElementById('logoutBtn');
    if (logoutBtn) {
        logoutBtn.addEventListener('click', () => {
            if (typeof window.logout === 'function') window.logout();
        });
    }

    // Cerrar modal con la X
    const closeLoginBtn = document.getElementById('closeLoginBtn');
    if (closeLoginBtn) {
        closeLoginBtn.addEventListener('click', () => {
            if (typeof window.closeLoginModal === 'function') window.closeLoginModal();
        });
    }

    // Logo click: go home or scroll to top if already on home
    const siteLogo = document.getElementById('siteLogo');
    if (siteLogo) {
        siteLogo.addEventListener('click', function(e) {
            const onHome = location.pathname === '/' || /\/index\.html$/i.test(location.pathname);
            if (onHome) {
                e.preventDefault();
                window.scrollTo({ top: 0, behavior: 'smooth' });
            }
        });
    }
});

